import * as React from "react";
import MessageDirection from "../../Enums/MessageDirection";
import { Persona, PersonaSize, PersonaPresence, TextField } from "office-ui-fabric-react";

export class Input extends React.Component<any, any> {

    constructor(props: any) {
        super(props);

        this.state = {
            text: ""
        }
    }

    onChange(e: any) {
        this.setState({ text: e.target.value });
    }

    onSubmit(e:any) {
        e.preventDefault();
        this.setState({text: ""});
        this.props.onSendMessage(this.state.text);
      }

    render(): JSX.Element {
        var className = this.props.message.Direction == MessageDirection.Incoming ? "Messages-message" : "Messages-message currentMember";
        return (
            <div className="Input">
                <form onSubmit={e => this.onSubmit(e)}>
                    <TextField
                        autoFocus={true}
                        onChange={e => this.onChange(e)}
                        value={this.state.text}
                        type="multiline"
                        placeholder="Enter your message and press ENTER"
                    />
                    <button>Send</button>
                </form>
            </div>
        );
    }
}
